head.script:

      function closeopen(elementId) {
        var visible = document.getElementById(elementId);

        if(elementId.match("span")) {
	  var hidden = document.getElementById(elementId.replace("span","conclusion"));
	  hidden.style.display="block";
	  visible.style.display="none";
        } else {
	  var hidden = document.getElementById(elementId.replace("conclusion","span"));
	  hidden.style.display="block";
	  visible.style.display="none";
       }
     }
